SELECT JSON_OBJECT(
'metadata_version': 2,
'minReaderVersion': @{activity('Get Table Details').output.firstRow.minReaderVersion},
'container_name': SUBSTRING('@{activity('Get Table Details').output.firstRow.location}', 9, CHARINDEX('@','@{activity('Get Table Details').output.firstRow.location}')-9),
'table_location': SUBSTRING('@{activity('Get Table Details').output.firstRow.location}',  CHARINDEX('__unitystorage/','@{activity('Get Table Details').output.firstRow.location}'), len('@{activity('Get Table Details').output.firstRow.location}'))
) as source_metadata;
